				$hours = plugin.$bigMaster.find('.fdp-time-h'),
				$mins = plugin.$bigMaster.find('.fdp-time-m');

			$year.text(date.year());
			$month.text(MONTHS_LONG[date.month()]);
			$dayStr.text(WEEKDAYS_SHORT[date.weekday()]);
